mapper.select:
select count(*)
from maeil_member
where employee_id=#{employee_id}

mapper.update:
update maeil_member
set employee_email=#{employee_email}, department_name=#{department_name}
where employee_id=#{employee_id}

update maeil_member
set employee_password=#{newpwd}
where employee_id=#{employee_id} and employee_password=#{employee_password}
